{
  "category": "Leave and Attendance",
  "subject": "Leave balance inquiry",
  "description": "I need to check my remaining leave balance for this year.",
  "priority": "medium"
}

{
  "message": "I would like to provide additional information about my issue."
}

{
  "message": "We are looking into your issue and will resolve it soon.",
  "isInternal": false
}

{
  "resolution": "Issue has been resolved by adjusting the salary calculation.",
  "actionTaken": "Corrected the overtime calculation formula"
}

{
  "companyName": "TechnoRizen Inc.",
  "companyLogo": "/uploads/logo.png",
  "emailSettings": {
    "smtpServer": "smtp.example.com",
    "smtpPort": 587,
    "smtpUsername": "notifications@technorizen.com",
    "smtpPassword": "password123",
    "fromEmail": "hr@technorizen.com",
    "fromName": "TechnoRizen HR"
  },
  "notificationSettings": {
    "emailNotifications": true,
    "smsNotifications": false,
    "pushNotifications": true
  },
  "securitySettings": {
    "passwordPolicy": {
      "minLength": 8,
      "requireUppercase": true,
      "requireLowercase": true,
      "requireNumbers": true,
      "requireSpecialChars": true
    },
    "sessionTimeout": 30,
    "twoFactorAuth": false
  }
}

{
  "message": "Important announcement: Office will be closed on Friday for maintenance.",
  "recipients": ["all"],
  "priority": "high"
}

{
  "name": "leave_approval",
  "subject": "Leave Request {{status}}",
  "emailTemplate": "<p>Dear {{name}},</p><p>Your leave request from {{startDate}} to {{endDate}} has been {{status}}.</p><p>Regards,<br>HR Team</p>",
  "pushTemplate": "Your leave request has been {{status}}."
}
